import React, { useState } from "react";
import { Box, TextField, Button, Typography, Card, Alert } from "@mui/material";
import FavoriteIcon from "@mui/icons-material/Favorite";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Signup() {
  const [form, setForm] = useState({ name: "", email: "", password: "" });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async () => {
    setError(""); setSuccess("");
    try {
      const res = await axios.post("http://localhost:5000/signup", form, {
        headers: { "Content-Type": "application/json" }
      });
      setSuccess("Signup successful! Redirecting to login...");
      setTimeout(() => navigate("/login"), 1500);
    } catch (err) {
      setError(err.response?.data?.error || "Signup failed");
    }
  };

  return (
    <Box sx={{
      minHeight: "100vh",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      background: "linear-gradient(135deg, #f9d3e1, #f8a1c4)"
    }}>
      <Card sx={{ p: 4, borderRadius: 4, boxShadow: 3, width: 360, textAlign: "center" }}>
        <FavoriteIcon sx={{ color: "#e91e63", fontSize: 50 }} />
        <Typography variant="h5" fontWeight="bold" gutterBottom>Sign Up</Typography>
        <TextField fullWidth label="Name" name="name" sx={{ mb: 2 }} onChange={handleChange} />
        <TextField fullWidth label="Email" name="email" sx={{ mb: 2 }} onChange={handleChange} />
        <TextField fullWidth label="Password" type="password" name="password" sx={{ mb: 2 }} onChange={handleChange} />
        <Button variant="contained" color="secondary" fullWidth onClick={handleSubmit}>Sign Up</Button>
        {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
        {success && <Alert severity="success" sx={{ mt: 2 }}>{success}</Alert>}
      </Card>
    </Box>
  );
}
