import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Signup() {
  const [form, setForm] = useState({ name: "", email: "", password: "" });
  const navigate = useNavigate();

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async () => {
    try {
      const res = await axios.post("http://localhost:5000/signup", form);
      alert("üåø " + res.data.msg);
      navigate("/login");
    } catch (err) {
      alert("‚ö†Ô∏è " + (err.response?.data?.error || "Signup failed"));
    }
  };

  return (
    <div style={styles.bg}>
      <div style={styles.card}>
        <h1 style={styles.title}>üíì Hridamrit</h1>
        <h3 style={styles.subtitle}>Create Your Account</h3>

        <input style={styles.input} name="name" placeholder="Full Name" onChange={handleChange} />
        <input style={styles.input} name="email" placeholder="Email Address" onChange={handleChange} />
        <input style={styles.input} type="password" name="password" placeholder="Password (min 8 chars)" onChange={handleChange} />

        <button style={styles.button} onClick={handleSubmit}>Sign Up</button>
        <p style={styles.footer}>Already have an account? <a href="/login" style={styles.link}>Login</a></p>
      </div>
    </div>
  );
}

const styles = {
  bg: {
    minHeight: "100vh",
    background: "linear-gradient(135deg, #c6f6d5, #bee3f8)",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontFamily: "Poppins, sans-serif",
  },
  card: {
    background: "#fff",
    padding: "40px",
    borderRadius: "20px",
    boxShadow: "0 8px 30px rgba(0,0,0,0.1)",
    width: "320px",
    textAlign: "center",
  },
  title: { color: "#2b6cb0", marginBottom: "5px" },
  subtitle: { color: "#4a5568", marginBottom: "20px" },
  input: {
    width: "100%",
    padding: "10px",
    margin: "8px 0",
    borderRadius: "10px",
    border: "1px solid #a0aec0",
    fontSize: "15px",
    outline: "none",
  },
  button: {
    width: "100%",
    padding: "10px",
    backgroundColor: "#2b6cb0",
    color: "white",
    border: "none",
    borderRadius: "10px",
    cursor: "pointer",
    marginTop: "10px",
    fontSize: "16px",
    transition: "0.3s",
  },
  footer: { marginTop: "15px", fontSize: "14px" },
  link: { color: "#2b6cb0", textDecoration: "none" },
};
