import React, { useState } from "react";
import {
  Box,
  Button,
  TextField,
  Typography,
  Card,
  CardContent,
  Alert,
} from "@mui/material";
import FavoriteIcon from "@mui/icons-material/Favorite";
import { useNavigate } from "react-router-dom";

export default function Signup() {
  const navigate = useNavigate();

  const [form, setForm] = useState({ name: "", email: "", password: "" });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Handle input change
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    setError("");
    setSuccess("");
  };

  // Email validation
  const validateEmail = (email) => {
    const emailRegex = /^[\w.%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,}$/;
    return emailRegex.test(email);
  };

  // Password validation
  const validatePassword = (password) => {
    const passRegex =
      /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
    return passRegex.test(password);
  };

  const handleContinue = () => {
    if (!form.name.trim()) {
      setError("âš ï¸ Please enter your name.");
      return;
    }
    if (!validateEmail(form.email)) {
      setError("âš ï¸ Please enter a valid email (e.g., example@gmail.com).");
      return;
    }
    if (!validatePassword(form.password)) {
      setError(
        "âš ï¸ Password must be at least 8 characters, include one uppercase, one lowercase, one number, and one special symbol."
      );
      return;
    }

    // âœ… If all checks pass
    setSuccess("âœ… Signup successful! Redirecting...");
    setTimeout(() => navigate("/predict"), 1200);
  };

  return (
    <Box
      sx={{
        minHeight: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        background: "linear-gradient(135deg, #f9d3e1, #f8a1c4)",
        fontFamily: "Poppins, sans-serif",
      }}
    >
      <Card
        sx={{
          p: 4,
          borderRadius: 4,
          boxShadow: 4,
          width: 380,
          textAlign: "center",
          backgroundColor: "#fff",
        }}
      >
        <FavoriteIcon sx={{ color: "#e91e63", fontSize: 48 }} />
        <Typography variant="h5" fontWeight="bold" gutterBottom>
          Welcome to Hridamrit ðŸ’–
        </Typography>
        <Typography variant="body2" color="text.secondary" mb={2}>
          Start your heart wellness journey today.
        </Typography>

        <CardContent>
          <TextField
            fullWidth
            label="Name"
            name="name"
            variant="outlined"
            sx={{ mb: 2 }}
            value={form.name}
            onChange={handleChange}
          />
          <TextField
            fullWidth
            label="Email"
            name="email"
            type="email"
            variant="outlined"
            sx={{ mb: 2 }}
            value={form.email}
            onChange={handleChange}
          />
          <TextField
            fullWidth
            label="Password"
            name="password"
            type="password"
            variant="outlined"
            sx={{ mb: 3 }}
            value={form.password}
            onChange={handleChange}
          />

          {error && (
            <Alert severity="error" sx={{ mb: 2, fontSize: "0.9rem" }}>
              {error}
            </Alert>
          )}
          {success && (
            <Alert severity="success" sx={{ mb: 2, fontSize: "0.9rem" }}>
              {success}
            </Alert>
          )}

          <Button
            variant="contained"
            color="secondary"
            fullWidth
            sx={{
              py: 1.2,
              borderRadius: 2,
              fontWeight: "bold",
              backgroundColor: "#e91e63",
              "&:hover": { backgroundColor: "#d81b60" },
            }}
            onClick={handleContinue}
          >
            Continue to Prediction
          </Button>
        </CardContent>
      </Card>
    </Box>
  );
}
