import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [manualInput, setManualInput] = useState({
    age: "",
    gender: "",
    ap_hi: "",
    ap_lo: "",
    cholesterol: "",
    gluc: "",
    smoke: "",
    alco: "",
  });
  const [fitData, setFitData] = useState({});
  const [result, setResult] = useState(null);
  const [token, setToken] = useState(localStorage.getItem("googleFitToken") || "");

  // Receive token from popup
  useEffect(() => {
    window.addEventListener("message", (event) => {
      if (event.origin.includes("localhost:3000") && event.data.token) {
        localStorage.setItem("googleFitToken", event.data.token);
        setToken(event.data.token);
        alert("âœ… Google Fit connected!");
      }
    });
  }, []);

  const handleChange = (e) => {
    setManualInput({ ...manualInput, [e.target.name]: e.target.value });
  };

  const handleLogin = () => {
    window.open("http://localhost:5000/login", "_blank", "width=500,height=600");
  };

  const handlePredict = async () => {
    if (!token) {
      alert("Please login with Google Fit first!");
      return;
    }

    // Basic validation
    const emptyField = Object.keys(manualInput).find((key) => manualInput[key] === "");
    if (emptyField) {
      alert(`Please fill ${emptyField}`);
      return;
    }

    try {
      const res = await axios.post("http://localhost:5000/predict_fit", {
        ...manualInput,
        token,
      });
      setFitData(res.data.fit_data);
      setResult(res.data);
    } catch (err) {
      console.error(err);
      alert("Prediction failed. Check backend console.");
    }
  };

  return (
    <div style={{ padding: "30px", fontFamily: "Poppins, sans-serif" }}>
      <h2>ðŸ©º Hridamrit â€” Heart Attack Risk Prediction</h2>

      <div style={{ marginBottom: "15px" }}>
        <button onClick={handleLogin} style={{ marginRight: "10px" }}>
          Login with Google Fit
        </button>
        <button onClick={handlePredict}>Predict</button>
      </div>

      <h3>Enter Your Details</h3>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
          gap: "10px",
          maxWidth: "600px",
        }}
      >
        {Object.keys(manualInput).map((key) => (
          <div key={key}>
            <label style={{ textTransform: "capitalize" }}>{key}:</label>
            <input
              type="number"
              name={key}
              value={manualInput[key]}
              onChange={handleChange}
              style={{
                width: "100%",
                padding: "8px",
                borderRadius: "8px",
                border: "1px solid #ccc",
              }}
            />
          </div>
        ))}
      </div>

      {fitData.avg_steps && (
        <div style={{ marginTop: "25px" }}>
          <h3>ðŸ“Š Google Fit Data</h3>
          <pre>{JSON.stringify(fitData, null, 2)}</pre>
        </div>
      )}

      {result && (
        <div style={{ marginTop: "25px" }}>
          <h3>ðŸ§  Prediction Result</h3>
          <p>
            Predicted Class:{" "}
            <strong>{result.predicted_class === 1 ? "High Risk" : "Low Risk"}</strong>
          </p>
          <p>Probability: {result.probability}</p>
        </div>
      )}
    </div>
  );
}

export default App;
