import React, { useState } from "react";
import axios from "axios";
import {
  Box,
  TextField,
  Button,
  Typography,
  Card,
  CardContent,
  Grid,
  Alert,
  Divider
} from "@mui/material";
import FavoriteIcon from "@mui/icons-material/Favorite";

export default function Predict() {
  const [inputs, setInputs] = useState({
    age: "",
    gender: "",
    ap_hi: "",
    ap_lo: "",
    cholesterol: "",
    gluc: "",
    smoke: "",
    alco: "",
    bmi: ""
  });

  const [output, setOutput] = useState(null);
  const [loading, setLoading] = useState(false);

  // Handle input field change
  const handleChange = (e) => {
    setInputs({ ...inputs, [e.target.name]: e.target.value });
  };

  // Submit form data
  const handleSubmit = async () => {
    // ‚úÖ Convert all inputs to valid numbers (avoid empty strings)
    const cleanData = {};
    Object.keys(inputs).forEach((key) => {
      const value = parseFloat(inputs[key]);
      cleanData[key] = isNaN(value) ? 0 : value;
    });

    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/predict_fit", cleanData, {
        headers: { "Content-Type": "application/json" },
      });
      setOutput(res.data);
    } catch (err) {
      console.error(err);
      alert("‚ö†Ô∏è Error fetching prediction. Please ensure your Flask backend is running.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box
      sx={{
        minHeight: "100vh",
        background: "linear-gradient(135deg, #f8e1ec, #fbe6f2)",
        py: 5,
        px: 3,
      }}
    >
      {/* Title Section */}
      <Box sx={{ textAlign: "center", mb: 4 }}>
        <FavoriteIcon sx={{ color: "#e91e63", fontSize: 60 }} />
        <Typography variant="h4" fontWeight="bold">
          Hridamrit üíñ ‚Äî Heart Health Predictor
        </Typography>
        <Typography variant="subtitle1" color="text.secondary">
          Know your heart‚Äôs condition and get tailored health advice.
        </Typography>
      </Box>

      {/* Input Form */}
      <Card sx={{ maxWidth: 800, mx: "auto", p: 3, borderRadius: 4, boxShadow: 3 }}>
        <Typography variant="h6" gutterBottom>
          üìù Enter Your Health Details
        </Typography>
        <Grid container spacing={2}>
          {Object.keys(inputs).map((key) => (
            <Grid item xs={12} sm={6} md={4} key={key}>
              <TextField
                fullWidth
                type="number"
                label={key.toUpperCase()}
                name={key}
                value={inputs[key]}
                onChange={handleChange}
                variant="outlined"
                size="small"
              />
            </Grid>
          ))}
        </Grid>

        <Box textAlign="center" mt={3}>
          <Button
            variant="contained"
            color="secondary"
            onClick={handleSubmit}
            sx={{ px: 4, py: 1.2, borderRadius: 3 }}
            disabled={loading}
          >
            {loading ? "Predicting..." : "Predict"}
          </Button>
        </Box>
      </Card>

      {/* Results Section */}
      {output && (
        <Box sx={{ maxWidth: 800, mx: "auto", mt: 5 }}>
          <Divider sx={{ mb: 3 }} />
          <Typography
            variant="h5"
            fontWeight="bold"
            textAlign="center"
            gutterBottom
          >
            ü©∫ Prediction Overview
          </Typography>

          <Grid container spacing={3}>
            {/* Fit Data Card */}
            <Grid item xs={12} sm={6}>
              <Card sx={{ borderRadius: 4, boxShadow: 2, backgroundColor: "#f9f9f9" }}>
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    üìä Fit Data
                  </Typography>
                  <Typography>Steps (avg/day): {output.fit_data.avg_steps.toFixed(1)}</Typography>
                  <Typography>Calories (avg/day): {output.fit_data.avg_calories.toFixed(1)}</Typography>
                  <Typography>Height: {output.fit_data.height.toFixed(2)} m</Typography>
                  <Typography>Weight: {output.fit_data.weight.toFixed(1)} kg</Typography>
                </CardContent>
              </Card>
            </Grid>

            {/* Prediction Result Card */}
            <Grid item xs={12} sm={6}>
              <Card
                sx={{
                  borderRadius: 4,
                  boxShadow: 2,
                  backgroundColor:
                    output.predicted_class === 1 ? "#ffe6e6" : "#e8f5e9",
                }}
              >
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    üí° Result
                  </Typography>
                  <Typography>
                    Predicted Class:{" "}
                    <strong>
                      {output.predicted_class === 1 ? "High Risk" : "Low Risk"}
                    </strong>
                  </Typography>
                  <Typography>
                    Probability: {(output.probability * 100).toFixed(2)}%
                  </Typography>

                  {output.predicted_class === 1 ? (
                    <Alert severity="error" sx={{ mt: 2 }}>
                      ‚ö†Ô∏è High risk detected! Please consult a doctor.
                    </Alert>
                  ) : (
                    <Alert severity="success" sx={{ mt: 2 }}>
                      ‚ù§Ô∏è You are at low risk. Keep up your healthy habits!
                    </Alert>
                  )}
                </CardContent>
              </Card>
            </Grid>

            {/* Recommendations */}
            <Grid item xs={12}>
              <Card
                sx={{
                  borderRadius: 4,
                  boxShadow: 2,
                  backgroundColor: "#fffaf0",
                  p: 2,
                }}
              >
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    ‚úÖ Precautions & Recommendations
                  </Typography>
                  {output.predicted_class === 1 ? (
                    <ul>
                      <li>Exercise at least 30 mins daily üèÉ‚Äç‚ôÄÔ∏è</li>
                      <li>Limit salt and sugar intake üçΩÔ∏è</li>
                      <li>Avoid smoking or alcohol üö≠</li>
                      <li>Monitor blood pressure regularly ‚è±Ô∏è</li>
                    </ul>
                  ) : (
                    <ul>
                      <li>Maintain daily physical activity üö∂‚Äç‚ôÄÔ∏è</li>
                      <li>Eat balanced, heart-friendly meals ü•ó</li>
                      <li>Stay hydrated and sleep 7‚Äì8 hours üò¥</li>
                      <li>Continue regular health checkups ü©∫</li>
                    </ul>
                  )}
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </Box>
      )}
    </Box>
  );
}
