import { useState } from "react";
import axios from "axios";

axios.defaults.baseURL = "http://localhost:5000";
axios.defaults.withCredentials = true;

function App() {
  const [inputs, setInputs] = useState({});
  const [output, setOutput] = useState(null);

  const handleChange = (e) => setInputs({ ...inputs, [e.target.name]: e.target.value });

  const handleSubmit = async () => {
    try {
      const res = await axios.post("/predict_fit", inputs, {
        headers: { "Content-Type": "application/json" },
        withCredentials: true,
      });
      setOutput(res.data);
    } catch (err) {
      console.error(err);
      alert("Error! Make sure you are logged in via Google Fit first.");
    }
  };

  const handleLogin = () => {
    window.open("http://localhost:5000/login", "_blank");
    alert("Login tab opened â€” complete Google login, then click Predict.");
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>ðŸ’“ Hridamrit - Health Predictor</h1>
      <button onClick={handleLogin}>Login with Google Fit</button>
      <button onClick={handleSubmit} style={{ marginLeft: "10px" }}>Predict</button>

      {output && (
        <div style={{ marginTop: "30px" }}>
          <h2>ðŸ“Š Fit Data</h2>
          <p>Steps: {output.fit_data.avg_steps}</p>
          <p>Calories: {output.fit_data.avg_calories}</p>
          <p>Height: {output.fit_data.height}</p>
          <p>Weight: {output.fit_data.weight}</p>

          <h2>ðŸ©º Prediction</h2>
          <p>Result: {output.predicted_class === 1 ? "High Risk" : "Low Risk"}</p>
          <p>Probability: {(output.probability * 100).toFixed(2)}%</p>
        </div>
      )}
    </div>
  );
}

export default App;
