import { useState } from "react";
import axios from "axios";
import "./predict.css"; // Import the animation + background

export default function Predict() {
  const [inputs, setInputs] = useState({
    age: "", gender: "", ap_hi: "", ap_lo: "", cholesterol: "", gluc: "",
    smoke: "", alco: "", bmi: ""
  });
  const [output, setOutput] = useState(null);

  const handleChange = (e) => setInputs({ ...inputs, [e.target.name]: e.target.value });

  const handleSubmit = async () => {
    try {
      const res = await axios.post("http://localhost:5000/predict_fit", inputs, {
        headers: { "Content-Type": "application/json" }
      });
      setOutput(res.data);
    } catch (err) {
      alert("âš  Error fetching prediction. Make sure backend is running on port 5000.");
      console.error(err);
    }
  };

  return (
    <div style={styles.bg}>
      <div style={styles.overlay}>
        <div style={styles.card}>
          <h1 style={styles.title}>ðŸ©º Hridamrita Heart Risk Prediction</h1>
          <div style={styles.form}>
            {Object.keys(inputs).map((key) => (
              <input
                key={key}
                name={key}
                placeholder={key}
                style={styles.input}
                value={inputs[key]}
                onChange={handleChange}
              />
            ))}
            <button style={styles.button} onClick={handleSubmit}>Predict</button>
          </div>
          {output && (
            <div style={styles.result}>
              <h3>Result:</h3>
              <p><b>Risk:</b> {output.predicted_class === 1 ? "High ðŸ’”" : "Low ðŸ’–"}</p>
              <p><b>Probability:</b> {(output.probability * 100).toFixed(2)}%</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

const styles = {
  bg: {
    minHeight: "100vh",
    backgroundImage: "url('/heart-bg.png')",
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontFamily: "Poppins, sans-serif",
    animation: "pulseHeart 3s infinite ease-in-out",
  },
  overlay: {
    backgroundColor: "rgba(0,0,0,0.55)",
    padding: "60px",
    borderRadius: "20px",
    backdropFilter: "blur(5px)",
  },
  card: {
    textAlign: "center",
    color: "#fff",
    width: "360px",
  },
  title: { fontSize: "2rem", marginBottom: "20px", color: "#ff4d4d" },
  form: { display: "flex", flexDirection: "column" },
  input: {
    padding: "10px",
    margin: "6px 0",
    borderRadius: "8px",
    border: "1px solid #ccc",
    fontSize: "14px",
  },
  button: {
    marginTop: "10px",
    padding: "10px",
    backgroundColor: "#ff4d4d",
    color: "#fff",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    fontSize: "16px",
  },
  result: { marginTop: "20px", color: "#fff" },
};
