import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const [form, setForm] = useState({ email: "", password: "" });
  const navigate = useNavigate();

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async () => {
    try {
      const res = await axios.post("http://localhost:5000/login", form);
      alert("üí´ " + res.data.msg);
      navigate("/predict");
    } catch (err) {
      alert("‚ùå " + (err.response?.data?.error || "Login failed"));
    }
  };

  return (
    <div style={styles.bg}>
      <div style={styles.overlay}>
        <div style={styles.card}>
          <h1 style={styles.title}>üíì Welcome Back</h1>
          <h3 style={styles.subtitle}>Login to Hridamrit</h3>

          <input style={styles.input} name="email" placeholder="Email Address" onChange={handleChange} />
          <input style={styles.input} type="password" name="password" placeholder="Password" onChange={handleChange} />

          <button style={styles.button} onClick={handleSubmit}>Login</button>
          <p style={styles.footer}>New here? <a href="/" style={styles.link}>Create Account</a></p>
        </div>
      </div>
    </div>
  );
}

const styles = {
  bg: {
    minHeight: "100vh",
    backgroundImage: "url('/heart-bg.png')",
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontFamily: "Poppins, sans-serif",
    animation: "pulseHeart 3s infinite ease-in-out",
  },
  overlay: {
    backgroundColor: "rgba(0,0,0,0.55)",
    padding: "60px",
    borderRadius: "20px",
    backdropFilter: "blur(5px)",
  },
  card: {
    textAlign: "center",
    color: "#fff",
    width: "320px",
  },
  title: { fontSize: "2.2rem", marginBottom: "5px", color: "#ff4d4d" },
  subtitle: { color: "#eee", marginBottom: "25px" },
  input: {
    width: "100%",
    padding: "10px",
    margin: "8px 0",
    borderRadius: "10px",
    border: "1px solid #ccc",
    fontSize: "15px",
  },
  button: {
    width: "100%",
    padding: "10px",
    backgroundColor: "#ff4d4d",
    color: "#fff",
    border: "none",
    borderRadius: "10px",
    fontSize: "16px",
    cursor: "pointer",
    marginTop: "10px",
  },
  footer: { marginTop: "15px", fontSize: "14px" },
  link: { color: "#ff6b6b", textDecoration: "none" },
};
